import lstm3
import pandas as pd
import pytest


lst_1 = ['5/24/17', '5/25/17', '5/26/17', '5/27/17', '5/28/17']
lst_2 = [0, 274128890, 0, 0, 361187510]
lst_3 = [49885.3, 141787.9, 17306.0, 29869.5, 162475.8]
lst_4 = [2004122, 1854564, 1410995, 1874123, 1769373]
col_1 = 'date'
col_2 = 'tv'
col_3 = 'display_impressions'
col_4 = 'sales'

df = pd.DataFrame({col_1: lst_1,col_2: lst_2,col_3: lst_3,col_4: lst_4})

def test_1():
    df = pd.DataFrame({col_1: lst_1,col_2: lst_2,col_3: lst_3,col_4: lst_4})
    tt = lstm3.lstm_ttsplit(df)
    assert len(tt) == 4

def test_2():
    df = pd.DataFrame({col_1: lst_1,col_2: lst_2,col_3: lst_3,col_4: lst_4})
    tup = lstm3.lstm_ttsplit(df)
    y = lstm3.lstm_run(tup[0],tup[1],tup[2],tup[3])
    assert len(y[0]) == 500
